function gaFunnel(funnel) {
    if ( funnel === "Lead") {
        return "generate_lead";
    } else if ( funnel === "ViewContent") {
        return "view_item";
    } else {
        return "page_view";
    }
}

function fbFunnel(funnel) {
    if ( funnel === "Lead" || funnel === "ViewContent") {
        return funnel;
    } else {
        return "PageView";
    }
}

